import React from 'react';
import './Card.css';

function Card(props) {
    let card = props.card; //props.info = {img:"", title:"", year:"", desc:""}

    // onMouseEnter

    // onMouseLeave

    return (
        <div className="card" style={{backgroundImage:url('https://cdn.britannica.com/55/174255-050-526314B6/brown-Guernsey-cow.jpg')}}>
            {/* <img src="https://cdn.britannica.com/55/174255-050-526314B6/brown-Guernsey-cow.jpg" /> */}
            <div className="text">
                <h2>{card.URL ? <a href={card.URL}>{card.Heading}</a> : card.Heading}</h2>
                <p>{card.DateRange}</p>
                <h4>{card.Subtitle}</h4>
                {/* <p>{card.Description}</p> */}
            </div>
        </div>
    );
}

export default Card;
