import React from 'react';
import './Card.css';

function Card(props) {
    let card = props.card; 
    // when thing is clicked it should create popup with more details

    // onMouseEnter

    // onMouseLeave

    return (
        <div className={`card ${props.className}`}>
            {/* <img className="card__image" alt="cow" src="https://cdn.britannica.com/55/174255-050-526314B6/brown-Guernsey-cow.jpg" /> */}
            <div className="card__text">
                <h2>{card.URL ? <a href={card.URL}>{card.Heading}</a> : card.Heading}</h2>
                <p>{card.DateRange}</p>
                <h4>{card.Subtitle}</h4>
                {/* <p>{card.Description}</p> */}
            </div>
        </div>
    );
}

export default Card;
