import React from 'react';
import FeatherIcon from 'feather-icons-react';
import './Badges.css'

function Badges({ links }) {
    const badgeItems = links.map((c) =>
        <a href={c.URL}>
            <div className="badges__badge">
                <div className="badges__icon" style={`background-color:${c.Color}`}>
                    <FeatherIcon icon={c.Icon} />
                </div>
                <p>{c.Platform}</p>
            </div>
        </a>
    );

    return (
        <div className="badges">
            {badgeItems}
        </div>
    )
}

export default Badges;
