import React from 'react';
import './History.css';
import Card from "./Card"
import Profile from "./Profile"
import Badges from './Badges'
import * as data from "./Background.json";
import Images from "./Images";

function History(props) {

    function CreateCard(section, identity) {
        return section.map((item) =>
            <Card className={identity} card={item} />
        );
    }

    // make this take image instead
    const imageCards = Images.map((i) =>
        <Card image={i} />
    );


    // let experience = CreateCard(data.experience, "experience");
    // let projects = CreateCard(data.projects, "projects");
    // let activities = CreateCard(data.activities, "activities");
    // let education = CreateCard(data.education, "education");

    return (
        <div className="history__content">
            <Profile bio={data.profile.bio} />
            <Badges social={data.profile.social} />
            <hr />
            <div className="history__cards">
                {imageCards}
                { Images.map(({src, alt}) => <img src={src} alt={alt} />) }
                {/* {experience}
                {projects}
                {education}
                {activities} */}
            </div>
        </div>
    );
}

export default History;
